import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { allLogin } from '../redux/actions';

function Login() {
  const [emailLogin, setEmailLogin] = useState('');
  const [passwordLogin, setPasswordLogin] = useState('');
  const navigate = useNavigate();
  const dispatch = useDispatch();

  function isValid():boolean {
    return (/^[\d\w]{1,20}@[\w]{1,10}\.com$/i.test(emailLogin) && passwordLogin.length >= 6);
  }

  function OnClickEnter(e: React.FormEvent) {
    e.preventDefault();
    dispatch(allLogin(emailLogin));
    navigate('/carteira');
  }

  return (
    <div>
      <input
        onChange={ (e) => setEmailLogin(e.target.value) }
        type="text"
        data-testid="email-input"
        placeholder="Email"
      />
      <input
        onChange={ (e) => setPasswordLogin(e.target.value) }
        type="password"
        data-testid="password-input"
        placeholder="Senha"
      />
      <button disabled={ !isValid() } onClick={ OnClickEnter }>Entrar</button>
    </div>
  );
}

export default Login;
