import React, { useEffect, useState } from 'react'
import { mockAPI } from '../helpers/mockAPI';

function CryptoList() {

const [data, setData] = useState(null);

useEffect(() => {
    const fetchData = async () => {
    setData(mockAPI.data);
    };

    fetchData();
}, []);

function deleteBtnClick({ target: { id } }) {
    const addCrypto = data
    .filter((add) => Number(add.id) !== Number(id));
    return addCrypto
}

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
        </thead>
        <tbody>

          {data.map((exp: any) => (
            <tr key={ exp.id }>
              <td>{exp.description}</td>
              <td>{exp.tag}</td>
              <td>{exp.method}</td>
              <td>{Number(exp.value).toFixed(2)}</td>
              <td>{exp.exchangeRates[exp.currency].name}</td>
              <td>{Number(exp.exchangeRates[exp.currency].ask).toFixed(2)}</td>
              <td>
                {Number(exp.value * exp.exchangeRates[exp.currency].ask)
                  .toFixed(2)}
              </td>
              <td>Real</td>
              <td>
                <button
                  data-testid="delete-btn"
                  id={ exp.id }
                  onClick={ deleteBtnClick }
                >
                  Excluir
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CryptoList
