import React, { useEffect } from 'react';

function Wallet() {
  const [wallet, setWallet] = useState([]);

  useEffect(() => {
    const returnStorage = readStorage();
    setWallet(returnStorage);
  }, []);
  const readStorage = () => JSON
  .parse(localStorage.getItem('wallet')) || [];


  return (
    <div>
      {wallet &&
            wallet.map((coin) => (
            <tr key={ coin.id }>
              <td>{coin.name}</td>
              <td>{coin.symbol}</td>
              <td>{USDollar.format(coin.quote.USD.price)}</td>
              <td>{USDollar.format(coin.quote.USD.market_cap)}</td>
              <td>{USDollar.format(coin.total_supply)}</td>
              <td>
              </td>
              <td>
                <button
                  data-testid="add-btn"
                  id={ coin.name }
                  onClick={ removeBtnClick }
                >
                  X
                </button>
              </td>
            </tr>
          ))}
    </div>
  );
}

export default Wallet;
